</template>

<script>
    import { isMobile, isChrome, isIE } from "mobile-device-detect";
    import { gsap, Cubic, Power3, Back } from "gsap";

    export default {
        name: "Intro",

        data(){
          return {
              isEnd:false
          }
        },

        mounted() {
            // this.isEnd = true;

            if(this.isEnd){
                this.introComplete();
            } else {
                this.showIntro();
            }
        },

        props: ["isLoadComp"],

        methods: {

            showIntro(){

                let teaserKv = this.$el.querySelector( ".intro__wrap" );
                let container = teaserKv.querySelector( ".intro__content" );

                let intro = teaserKv.querySelector( ".intro__intro" );
                let content = intro.querySelector( "ul" );

                gsap.set(content, {y:"100%"});

                const TL = gsap.timeline({});
                TL.from( container, 0.3, { })
                TL.from( container, 0.6, { scale:0.3125, ease:Back.easeOut})
                TL.to( content, 1.3, { y:"1%", ease:Cubic.easeInOut }, "< -0.2")

                TL.to( content, 1, { y:"-20%", ease:Cubic.easeInOut, onComplete: () => {this.checkLoaded()} })
            },

            introStep_a(){

                let teaserKv = this.$el.querySelector( ".intro__wrap" );
                let intro = teaserKv.querySelector( ".intro__intro" );
                let content = intro.querySelector( "ul" );

                const TL = gsap.timeline({});
                TL.to( content, 1.0, { y:"-40%", ease:Cubic.easeInOut });
                TL.set(content, {y:0})
                TL.to( content, 1, { y:"-20%", ease:Cubic.easeInOut, onComplete: () => {this.checkLoaded()} })
            },

            introStep_b(){


                let teaserKv = this.$el.querySelector( ".intro__wrap" );
                let container = teaserKv.querySelector( ".intro__content" );

                let intro = teaserKv.querySelector( ".intro__intro" );
                let content = intro.querySelector( "ul" );

                let scale = ( isMobile ) ? "200vh" : "200vw";

                const TL = gsap.timeline({});
                TL.set( content.querySelector("li:nth-child(3)"), {opacity:0})
                TL.to( container, 1, { width:scale, height:scale, ease:Cubic.easeInOut} )
                TL.to( content.querySelector("li:first-child"), 0, { autoAlpha:0}, "<")
                TL.to( content.querySelector("li:nth-child(2) .slogan"), 1.3, {opacity:1, ease:Power3.easeInOut}, "< 0.0" )
                TL.to( teaserKv, 1, { y:-100+"%", ease:Cubic.easeInOut, onComplete:() => {  }}, "< 1.2" )
                TL.to( container, 1, { y:90+"vh", ease:Cubic.easeInOut, onComplete:() => { this.introComplete() }}, "<" )
            },

            checkLoaded(){

                if(!this.isLoadComp){
                    this.introStep_a();
                } else {
                    this.introStep_b();
                }
            },

            introComplete(){

                this.isEnd = true;
                document.querySelector( "html" ).classList.remove( "init-scroll" );
            }

        }
    }
</script>

